uploads.map:
function(doc) {
if(doc.type === 'document' && !doc.published)	
  emit(doc.user._id, {title: doc.title, _id:doc._id, _rev:doc._rev, creationDate: doc.creationDate, processed: doc.processed, uploaded: doc.uploaded || false, metadata:doc.metadata, template:doc.template});
}

metadata_by_template.map:
function(doc) {
  if(doc.type === 'document' && doc.template) {
    emit(doc.template, {_id: doc._id, metadata: doc.metadata});
  }
}

docs.map:
function(doc) {
if(doc.type === 'document')
  var newDoc = {};	
  for(var k in doc) {
   if(k !== 'fullText' && k !== 'file') {
    newDoc[k]=doc[k];
   }	
  }

  emit(doc._id, newDoc);
}

metadata_by_template.map:
function(doc) {
  if(doc.type === 'entity' && doc.template) {
    emit(doc.template, {_id: doc._id, metadata: doc.metadata});
  }
}

by_template.map:
function(doc) {
  if(doc.type === 'entity' && doc.template) {
    emit([doc.template, doc.language], {_id: doc._id, sharedId: doc.sharedId, title: doc.title, icon: doc.icon});
  }
}

by_language.map:
function(doc) {
if(doc.type === 'entity' || doc.type === 'document')	
  emit([doc.sharedId, doc.language], doc);
}

list.map:
function(doc) {
if(doc.type === 'page')	
  emit(doc.language, {title: doc.title, _id:doc._id, _rev:doc._rev, sharedId: doc.sharedId});
}

by_document.map:
function(doc) {
  if(doc.type == 'reference'){
    emit(doc.targetDocument, doc);
    emit(doc.sourceDocument, doc);	
  } 
}

uploads.map:
function(doc) {
if((doc.type === 'document' || doc.type === 'entity') && !doc.published)	
  emit([doc.user._id, doc.language], {type: doc.type, title: doc.title, sharedId: doc.sharedId, _id:doc._id, _rev:doc._rev, icon: doc.icon || {}, creationDate: doc.creationDate, processed: doc.processed, uploaded: doc.uploaded || false, metadata:doc.metadata, template:doc.template});
}

select_options.map:
function(doc) {
if(doc.type === 'thesauri')	
  emit(doc._id, {_id: doc._id, name: doc.name, type: 'thesauri'});
if(doc.type === 'entity') 
  emit(doc._id, {_id: doc._id, name: doc.title, type: 'entity'})
}
